const express = require('express');
const bodyParser = require('body-parser');
const nodemailer = require('nodemailer');

const app = express();

// Middleware pour analyser les données du formulaire
app.use(bodyParser.urlencoded({ extended: false }));
app.use(bodyParser.json());

// Route pour traiter les données du formulaire
app.post('/sendmail', (req, res) => {
  const { name, email, message } = req.body;

  // Configuration du transporteur SMTP pour Nodemailer (utilisez vos propres informations)
  const transporter = nodemailer.createTransport({
    service: 'gmail',
    auth: {
      user: 'mathis.truong95@gmail.com', // Remplacez par votre adresse Gmail
      pass: 'Mathis2004', // Remplacez par votre mot de passe Gmail
    },
  });

  // Contenu de l'e-mail
  const mailOptions = {
    from: email,
    to: 'mathis.truong95@gmail.com', // Remplacez par l'adresse e-mail de réception
    subject: `Nouveau message de ${name}`,
    text: `Nom: ${name}\nEmail: ${email}\n\nMessage:\n${message}`,
  };

  // Envoi de l'e-mail
  transporter.sendMail(mailOptions, (error, info) => {
    if (error) {
      console.log(error);
      res.status(500).send('Erreur lors de l\'envoi de l\'e-mail');
    } else {
      console.log('E-mail envoyé: ' + info.response);
      res.status(200).send('E-mail envoyé avec succès');
    }
  });
});

// Démarrez le serveur
const PORT = 5500; // Port à utiliser (peut être modifié)
app.listen(PORT, () => {
  console.log(`Serveur en cours d'exécution sur le port ${PORT}`);
});